CREATE TABLE companies (id SERIAL NOT NULL, country_id INT DEFAULT NULL, name VARCHAR(127) NOT NULL, city VARCHAR(63) NOT NULL, postcode VARCHAR(7) NOT NULL, address VARCHAR(63) NOT NULL, vatin VARCHAR(31) NOT NULL, ein VARCHAR(31) NOT NULL, bein VARCHAR(31) DEFAULT NULL, PRIMARY KEY(id));
